import React, { Component }  from 'react';
import {connect} from 'react-redux'
import {getData} from './Actions/getData'


class Form extends Component {
    constructor(props){
        super(props)

        this.state = {
            take: "new-product",
            product: {
                id: "new",
                brand: "",
                name: "",
                price: 0,
                currency: "",
                image_link: "",
                description: "",
                category: "",
                product_types: "",
                product_colors: "",
                tag_list: "",
                active: ""
            }
        }

        this.takeProduct = this.takeProduct.bind(this)
        this.handleChange = this.handleChange.bind(this)

    }

    componentDidMount(){
        this.props.getData() 
    }

    takeProduct(event){
        const take_product = this.props.data.data.filter(item => item.id === event)
        this.setState({
            take: event,
            product: event !== "new-product" ? take_product : {}
        })         
    }

    handleChange(ev, what){
        this.setState({
            ...this.state,
            item: {...this.state.item, [what]: ev}  
        })   
    }

    render(){ 
        const {data} = this.props.data
        return(
            <div>
                <select
                    onChange={(ev) => this.takeProduct(ev.target.value)}
                    value={this.state.take}>
                    <option 
                        key="new-product"
                        value="new-product">
                    new product
                    </option>
                    {data.map(item => 
                        <option 
                            key={item.id} 
                            value={item.id}>
                        {item.id}
                        </option>
                    )}
                </select>
                <b>Name: </b><br/>
                <input 
                    type="text" 
                    onChange={(ev) => this.handleChange(ev.target.value, 'name')}
                    value={this.state.product.name} /><br/>
                <b>Brand: </b><br/>
                <input 
                    type="text" 
                    onChange={(ev) => this.handleChange(ev.target.value, 'brand')} 
                    value={this.state.product.brand} /><br/>
                <b>Price: </b><br/>
                <input 
                    type="text"
                    onChange={(ev) => this.handleChange(ev.target.value, 'price')} 
                    value={this.state.product.price} /><br/>
                <b>Currency: </b>
                <p className="textDetails">{this.state.product.currency}</p>
                <b>Description: </b><br/>
                <textarea 
                    className="description"
                    onChange={(ev) => this.handleChange(ev.target.value, 'description')} 
                    value={this.state.product.description} /><br/>
                <b>Category: </b><br/>
                <input 
                    type="text" 
                    onChange={(ev) => this.handleChange(ev.target.value, 'category')} 
                    value={this.state.product.category} /><br/>
                <b>Type: </b><br/>
                <input 
                    type="text" 
                    onChange={(ev) => this.handleChange(ev.target.value, 'product_types')}
                    value={this.state.product.product_types} /><br/>
                <b>Tags: </b>
                <p className="textDetails">{this.state.product.tag_list}</p>
                <b>Colors: </b>
                {/* <div id="colors">
                    {this.state.product.product_colors.map(clr => 
                        <div 
                            key={clr.colour_name} 
                            className="color" 
                            style={{backgroundColor:clr.hex_value}}>
                        </div>)}
                </div> */}
            </div>
        )
    }
}

const mapStateToProps  = (state) => ({
    data: state.products,
})

export default connect(mapStateToProps, {getData})(Form)
