import { combineReducers } from 'redux'
import axios from 'axios'



const initialState = {
    data:[]
}

function imageExists(urlToFile){
    var xhr = new XMLHttpRequest();
    xhr.open('HEAD', urlToFile);
    xhr.send();

    if (xhr.status === "404") {
        console.log("File doesn't exist");
        return false;
    } else {
        console.log("File exists");
        return true;
    }

}

const products = (state = initialState, action) => {
    switch (action.type){
        case 'GET_DATA':
            const response = action.payload.map(item => {
                const {id, brand, name, price, currency, image_link, description, category, product_types, product_colors, tag_list} = item
                imageExists(image_link)
                const product = {
                    id: id,
                    brand: brand,
                    name: name,
                    price: price,
                    currency: currency,
                    //image: imageExists(image_link),
                    description: description,
                    category: category,
                    type: product_types,
                    colors: product_colors,
                    tag: tag_list
                }
                return product
            })

            return {
                ...state,
                data: response.reverse()
            }

        default:
            return state;
    }
}



export default combineReducers({ products })
