import {REQUEST, GET_DATA_SUCCESS, FAILURE, SORT_DATA, USERS_ERROR} from '../types'
import { createAction } from 'redux-api-middleware';

export const sortData = (text, sort, check, to, from) => dispatch => {
    try{
        const res = createAction({
            endpoint: 'http://localhost:5000',
            method: 'GET',
            headers: {
                "Accept": "application/json",
                "Content-Type": "application/json",
            },            
            types: [REQUEST, GET_DATA_SUCCESS, FAILURE]
        })

        if(sort === "Sortuj od A do Z"){
            sort = "name"
        }
        else if (sort === "Sortuj od Z do A"){
            sort = "reverse"
        }
        else{
            sort = "price"
        }
        dispatch( {
            type: SORT_DATA,
            text: text,
            sort: sort,
            check: check,
            to: to,
            from: from,
            payload: res.payload,
        })

    }
    catch(e){
        dispatch( {
            type: USERS_ERROR,
            payload: console.log(e),
        })
    }
}
