import React, { Component }  from 'react';
import {connect} from 'react-redux'
import {getData} from '../Actions/getData'
import {editProduct} from '../Actions/editProduct'
import {getProduct} from '../Actions/getProduct'
import './loadData.css'


class LoadData extends Component {
    constructor(props){
        super(props)

        this.state = {
            edit: []
        }

        this.handleProduct = this.handleProduct.bind(this)
        this.handleEdit = this.handleEdit.bind(this)
        this.handleDelete = this.handleDelete.bind(this)
    }

    componentDidMount(){
        this.props.getData() 
    }

    handleProduct(id, active){
        if (!active){
            this.props.history.push('/product')
            this.props.getProduct(id)
        }
    }

    handleEdit(id, item){
        this.props.editProduct(id)
        this.setState({
            edit: [...this.state.edit, item]
        })
    }

    handleDelete(){
        console.log("delete")
    }

    render() {
        const {data} = this.props.data

        return(
            <div className="view">
                {data.map(item => 
                    <div className="item" key={item.id} >
                        <div onClick={() => this.handleProduct(item.id, item.active)}>
                            <img src={item.image} alt={item.name} />
                            {!item.active && (
                                <div className="text">
                                    <p className="brand">{item.brand}</p> 
                                    <p className="name">{item.name}</p>
                                    <p className="category">Category: {item.category}</p>
                                    <p className="price">{"$" + item.price}</p> 
                                </div>
                            )}
                            {item.active && (
                                <div className="textEdit">
                                    <input type="text" value={item.brand} />
                                    <input type="text" value={item.name} />
                                    <input type="text" value={item.category} />
                                    <input type="text" value={item.price} />
                                </div>
                            )}
                        </div>
                        <div className="button">
                            <input
                                className="edit" 
                                type="button" 
                                value="Delete"
                                onClick={() => this.handleDelete()}/>
                            <input 
                                className="edit" 
                                type="button"
                                value="Edit"
                                onClick={() => this.handleEdit(item.id, item)}/>
                        </div>
                    </div>
                )}
            </div>
        )
    }
}

const mapStateToProps  = (state) => ({
    data: state.products,
})

export default connect(mapStateToProps, {getData, getProduct, editProduct})(LoadData)
