import {REQUEST, GET_DATA_SUCCESS, SORT_DATA, FAILURE} from '../types'
import { createAction } from 'redux-api-middleware';

export const sortData = (text, sort, check, to, from) => dispatch => {
    dispatch(createAction({
        endpoint: 'http://localhost:5000/sort',
        method: 'GET',
        headers: {
            "Accept": "application/json",
            "Content-Type": "application/json",
        },            
        types: [REQUEST, SORT_DATA, FAILURE]
    }))
    if(sort === "Sortuj od A do Z"){
        sort = "name"
    }
    else if (sort === "Sortuj od Z do A"){
        sort = "reverse"
    }
    else{
        sort = "price"
    }
    dispatch( {
        type: SORT_DATA,
        text: text,
        sort: sort,
        check: check,
        to: to,
        from: from,
    })
}
