import React, { Component }  from 'react';
import {connect} from 'react-redux'
import {getData} from '../Actions/getData'


const select = ["Sortuj od A do Z", "Sortuj od Z do A", "Sortuj przez cenę"]

class FilterData extends Component {
    constructor(props){
        super(props)

        this.state = {
            text: "",
            sort: "Sortuj od A do Z"
        }

        this.handleText = this.handleText.bind(this)
        this.handleSort = this.handleSort.bind(this)

    }

    handleText(event){
        this.setState({
            ...this.state,
            text: event
        })
    }

    handleSort(event){
        this.setState({
            ...this.state,
            sort: event
        })
    }

    render() {


        return(
            <div className="filter">
                <form>
                    <input type="text" value={this.state.text} onChange={(ev) => this.handleText(ev.target.value)} placeholder="wpisz nazwe produktu" /> 
                    <select onChange={(ev) => this.handleSort(ev.target)} value={this.state.sort}>
                        {select.map(item => 
                            <option key={item} value={item}>{item}</option>
                            )}

                    </select>
                </form>
            </div>
        )
    }
}

const mapStateToProps  = (state) => ({
    data: state.products,
})

export default connect(mapStateToProps, {getData})(FilterData)


// select z wyborem przez co sortowac(cena, alfabetycznie, odwrotnie do alfabetycznego)
