import React, { Component }  from 'react';
import {connect} from 'react-redux'
import {getData} from './Actions/getData'


class Form extends Component {
    constructor(props){
        super(props)

        this.state = {
            take: "new-product",
            product: {}
        }

        this.takeProduct = this.takeProduct.bind(this)
    }

    componentDidMount(){
        this.props.getData() 
    }

    takeProduct(event){
        console.log(this.state)
        this.setState({
            take: event,
            product: this.props.data.data.filter(item => item.id === event)
        })
    }

    render(){ 
        const {data} = this.props.data
        return(
            <div>
                <select
                    onChange={(ev) => this.takeProduct(ev.target.value)}
                    value={this.state.take}>
                    <option 
                        key="new-product"
                        value="new-product">
                    new product
                    </option>
                    {data.map(item => 
                        <option 
                            key={item.id} 
                            value={item.id}>
                        {item.id}
                        </option>
                    )}
                </select>

            </div>
        )
    }
}

const mapStateToProps  = (state) => ({
    data: state.products,
})

export default connect(mapStateToProps, {getData})(Form)
