import React, { Component} from 'react'
import FlipMove from 'react-flip-move';

import './SetDate.css'


class SetDate extends Component {
    constructor(props){
        super(props)

        this.itemDate =this.itemDate.bind(this);
    }


    itemDate(){
        if(this.props.active === "inactive"){
            return(
                <div></div>
            )
        }
        else{
            return(
                <FlipMove duration={250} easing="ease-in">
                    <div>
                        <p>Wybierz do kiedy chcesz zrobic zadanie: </p>      
                        <input
                            type ="date"
                            value = {this.props.dateTo}
                            onChange= {(event) => this.props.funDateTo(event.target.value)}
                        />
                        <input 
                            type = "button" 
                            onClick = {() => this.props.changeActive("","inactive")}
                            value ="Zatwierdz"/>
                    </div>
                </FlipMove>

            )
        }
    }




    render(){
        return(
            <div className={this.props.active}>
                {this.itemDate()}
            </div>
        )
    }

}

export default SetDate;
