import React from "react";
import './ToDo.css'
import FlipMove from 'react-flip-move';
import SetDate from './SetDate' 



const ToDo = (props) => {
    return(
        <div>
            <FlipMove duration={250} easing="ease-out">
                {props.list
                .map(choose =>
                    {if (choose.active === "deactivated"){  
                        return(
                            <div className="items" key = {choose.lp}>
                                <div  key = {choose.lp}>Lp {choose.lp}. Tekst: {choose.text}, Data: {choose.date}  </div>
                                <input 
                                    type = "button" 
                                    onClick = {() => props.deleteEvent(choose)}
                                    value ="Przycisk usuń"/>
                                <input
                                    type = "button" 
                                    onClick = {() => {choose.active = "activated"; props.addDone()}}
                                    value ="Przycisk zakończ zadanie"/>
                            </div>)}
                    else{
                        return (
                            <div className="items_active" key = {choose.lp}>Lp {choose.lp}. Tekst: {choose.text}, Data: {choose.date}</div>)
                    }    
                })}

            </FlipMove>
            <SetDate active={props.active} 
                     changeActive={props.changeActive}
                     dateTo = {props.dateTo}/>
        </div>
    )
}

export default ToDo;
