import React, {Component} from 'react'
import Formularz from './Formularz.js'


const reduce = (state, action) => {
    switch(action.type){
        case 'ADD':
            console.log([...state, action.add_new])
            return [...state, action.add_new]
        case 'DELETE':
            return state.filter(element => element.lp !== action.lp)
        case 'EXPIRED':
            return state.map(element => {
                if(element.lp === action.lp){
                    return {
                        ...element,
                        active: 'Expired'
                    }
                }
                return element

            })
        case 'DONE':
            console.log(state.map(element => {
                if(element.lp === action.lp){
                    return {
                        ...element,
                        active: 'Done'
                    }
                }
                return element

            }))
        case 'FILTER':
            if (action.box !== "All"){
                return (state.filter(function(item) {
                    return(item.active === action.box)
                }))
            }
            else{
                return state
            }
        case 'START':
            return state

    }
}



class Store extends Component {
    constructor(props){
        super(props)

        this.state = {
            immutableState: reduce([], {type: 'START'}),
            copyState: this.immutableState
        }

        this.setImmutableState = this.setImmutableState.bind(this)
        this.filter = this.filter.bind(this)
    }

    filter(event) {
        return this.state.copyState.filter(item => item.text.toLowerCase().startsWith(event.toLowerCase()))
    }

    setImmutableState(action){

        if(action.type !== 'FILTER'){
            this.setState({
                immutableState: reduce(this.state.immutableState, action),
                copyState: reduce(this.state.immutableState, action)
            })
        }
        else{
            this.setState({
                immutableState: reduce(this.filter(action.text), action)
            })

        }
    }



    render(){
        return(
            <div>
                <Formularz immutableState={this.state.immutableState} reduce={this.setImmutableState}/>
            </div>
        )
    }


}

export default Store
