import React from "react";
import './ToDo.css'
import FlipMove from 'react-flip-move';
import SetHour from './SetHour' 



class ToDo extends Component {
    constructor(props){
        super(props)


    }




    render(){
        return(
            <div>
                <FlipMove duration={250} easing="ease-out">
                    {props.list
                    .map(choose =>
                        {if (choose.active === "Todo"){  
                            return(
                                <div className="items" key = {choose.lp}>
                                    <div  key = {choose.lp}>
                                        Lp {choose.lp}. 
                                        Tekst: {choose.text}, 
                                        Data: {choose.date} , 
                                        Godzina: {choose.hour}  </div>
                                    <input 
                                        type = "button" 
                                        onClick = {() => props.deleteEvent(choose)}
                                        value ="Przycisk usuń"/>
                                    <input
                                        type = "button" 
                                        onClick = {() => {choose.active = "Done"; props.addDone()}}
                                        value ="Przycisk zakończ zadanie"/>
                                </div>
                            )}
                        else if(choose.active === "Done"){
                            return (
                                <div className="items_done" key = {choose.lp}>
                                    Lp {choose.lp}. 
                                    Tekst: {choose.text}, 
                                    Data: {choose.date}, 
                                    Godzina: {choose.hour}</div>)
                        }
                        else{
                            return (
                                <div className="items_expired" key = {choose.lp}>
                                    Lp {choose.lp}. 
                                    Tekst: {choose.text}, 
                                    Data: {choose.date}, 
                                    Godzina: {choose.hour}</div>)
                        }
                    })}

                </FlipMove>
                <SetHour {...props}/>
            </div>
        )
    }
}

export default ToDo;
