import React, { Component} from 'react'
import './SetHour.css'


class SetHour extends Component {
    constructor(props){
        super(props)

        this.state = {
            date: new Date()
        }

        this.itemDate = this.itemDate.bind(this);
        this.tick = this.tick.bind(this);
    }

    componentDidMount() {
        this.timerID = setInterval(
          () => this.tick(),
          1000
        );
    }


    componentWillUnmount() {
        clearInterval(this.timerID);
    }


    tick() {
        this.props.list.map(function(item){
            if(item.hour < this.state.date.toLocaleTimeString()){
                item.active = "Expired"
                return item
            }
            return item
        })
        this.setState({
            date: new Date()
        });
    }


    itemDate(){
        if(this.props.active === "inactive"){
            return(
                <div></div>
            )
        }
        else{
            return(
                <div>
                    <p>Wybierz godzine: </p>      
                    <input
                        type ="time"
                        value = {this.props.dateTo}
                        onChange= {(event) => this.props.funDateTo(event.target.value)}
                    />
                    <input 
                        type = "button" 
                        onClick = {() => this.props.changeActive("inactive")}
                        value ="Zatwierdz"/>
                </div>
            )
        }
    }


    render(){
        return(
            <div className={this.props.active}>
                {this.itemDate()}
            </div>
        )
    }

}

export default SetHour;
