import React, {useState} from "react";
import ToDo from './to-do.js'


const Formularz = () => {
    const [text, setText] = useState("");
    const [list, setList] = useState([]);
    const [done, setDone] = useState([]);
    const [date, setDate] = useState("");
    const [warning_date, setWarning_Date] = useState("");
    const [warning_text, setWarning_Text] = useState("");
    const months = {
        'Jan' : '01',
        'Feb' : '02',
        'Mar' : '03',
        'Apr' : '04',
        'May' : '05',
        'Jun' : '06',
        'Jul' : '07',
        'Aug' : '08',
        'Sep' : '09',
        'Oct' : '10',
        'Nov' : '11',
        'Dec' : '12'
    }




    function addText_to_List(event) {
        let now = Date(Date.now()).split(' ')
        let compare = now[3]+"-"+months[now[1]]+"-"+ now[2]
        if(text !== "" && date !== "" && Date.parse(compare) < Date.parse(date)){
            setList([...list, {lp: list.length !== 0 ? list[list.length-1]['lp'] + 1 : 1 ,text, date, active: "deactivated"}]);
            setDate("");
            setText("");
            setWarning_Date("");
            setWarning_Text("");
        }
        else if(Date.parse(compare) >= Date.parse(date)){
            setWarning_Date("UWAGA! Podano zla date");
        }
        else if(text === "" && date !== ""){
            setWarning_Text("UWAGA! Nie zawarto tekstu");
        }
        else if(date === "" && text !== ""){
            setWarning_Date("UWAGA! Nie zawarto daty");
        }
        else{
            setWarning_Date("UWAGA! Nie zawarto daty");
            setWarning_Text("UWAGA! Nie zawarto tekstu");
        }
    }

    function addDone(event){
        setDone([...done, {lp: event.lp, text: event.text, date: event.date, active: "activated"}])
    }

    function deleteEvent(event){
        let update = list.filter(a => a !== event)
        setList(update)
    }


    return(
        <div>
            <form>
                <input 
                    type = "text"
                    value = {text}
                    onChange ={(event) => {setText(event.target.value)}}
                />{warning_text}<br/>
                <input
                    type ="date"
                    value = {date}
                    onChange= {(event) => {setDate(event.target.value)}}
                />{warning_date}<br/>
                <input 
                    type = "button" 
                    onClick = {addText_to_List}
                    value ="Zatwierdz"/>
            </form>
            Lista to-do:
            <ToDo list = {list} deleteEvent = {deleteEvent} addDone = {addDone} />
        </div>
    )
}




export default Formularz;
