import React, {Component} from "react";
import './ToDo.css'
import FlipMove from 'react-flip-move';
import SetHour from './SetHour' 



class ToDo extends Component {
    constructor(props){
        super(props)

        this.state = {
            date: new Date()
        }

        this.itemDate = this.itemDate.bind(this);
        this.check = this.check.bind(this);
    }

    componentDidMount() {
        this.timerID = setInterval(
          () => this.tick(),
          1000
        );
    }


    componentWillUnmount() {
        clearInterval(this.timerID);
    }


    tick() {
        this.setState({
            date: new Date()
        });
    }


    check(){
        this.props.list.map((item) => {
            if(item.hour+":00" < this.state.date.toLocaleTimeString()){
                item.active = "Expired"
                return item
            }
            return item
        })
    }


    render(){
        return(
            <div>
                <FlipMove duration={250} easing="ease-out">
                    {this.props.list
                    .map(choose =>
                        {this.check()
                            if (choose.active === "Todo"){  
                            return(
                                <div className="items" key = {choose.lp}>
                                    <div  key = {choose.lp}>
                                        Lp {choose.lp}. 
                                        Tekst: {choose.text}, 
                                        Data: {choose.date} , 
                                        Godzina: {choose.hour}  </div>
                                    <input 
                                        type = "button" 
                                        onClick = {() => this.props.deleteEvent(choose)}
                                        value ="Przycisk usuń"/>
                                    <input
                                        type = "button" 
                                        onClick = {() => {choose.active = "Done"; this.props.addDone()}}
                                        value ="Przycisk zakończ zadanie"/>
                                </div>
                            )}
                        else if(choose.active === "Done"){
                            return (
                                <div className="items_done" key = {choose.lp}>
                                    Lp {choose.lp}. 
                                    Tekst: {choose.text}, 
                                    Data: {choose.date}, 
                                    Godzina: {choose.hour}</div>)
                        }
                        else{
                            return (
                                <div className="items_expired" key = {choose.lp}>
                                    Lp {choose.lp}. 
                                    Tekst: {choose.text}, 
                                    Data: {choose.date}, 
                                    Godzina: {choose.hour}</div>)
                        }
                    })}

                </FlipMove>
                <SetHour {...this.props}/>
            </div>
        )
    }
}

export default ToDo;
