import React, {useState} from "react";


const ToDo = (props) => {
    return(
        <div>
            {props.list
            .map(choose => (
                <div>
            <div onClick = {event => props.addDone(choose)} key = {choose.lp}>{choose.lp}. {choose.text}, {choose.date}</div>
            <input 
                    type = "button" 
                    onClick = {event => props.deleteEvent(choose)}
                    value ="Przycisk usuń"/>
            <input 
                    type = "button" 
                    onClick = {event => props.addDone(choose)}
                    value ="Przycisk zakończ zadanie"/>
            </div>))}
        </div>
    )
}

export default ToDo;
